import { drizzle } from "drizzle-orm/postgres-js";
import postgres from "postgres";
import * as schema from "@shared/schema";

// Create the connection
const connectionString = process.env.DATABASE_URL!;
const client = postgres(connectionString);
export const db = drizzle(client, { schema });

export type Database = typeof db;

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import OpenAI from 'openai';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { xai } from '@ai-sdk/xai';
import { generateText } from 'ai';

interface ConsultaVeicularRequest {
  placa?: string;
  chassis?: string;
  renavam?: string;
  tipo: 'gravame' | 'crv-digital' | 'base-estadual' | 'atpv-e';
}

interface ClienteLoginRequest {
  placa: string;
  cpf: string;
}

interface AdminLoginRequest {
  email: string;
  senha: string;
}

interface AssistenteRequest {
  pergunta: string;
  categoria?: string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  const JWT_SECRET = process.env.JWT_SECRET || 'fallback-secret-key';

  // Middleware to extract client IP
  const getClientIP = (req: Request): string => {
    return req.headers['x-forwarded-for'] as string || 
           req.headers['x-real-ip'] as string || 
           req.connection.remoteAddress || 
           '127.0.0.1';
  };

  // Rate limiting middleware
  const checkRateLimit = async (req: Request, res: Response, next: Function) => {
    const clientIP = getClientIP(req);
    const endpoint = req.path;

    const allowed = await storage.checkRateLimit(clientIP, endpoint);
    if (!allowed) {
      await storage.logSecurityEvent({
        action: 'rate_limit_exceeded',
        resource_type: 'api',
        ip_address: clientIP,
        user_agent: req.headers['user-agent'],
        details: { endpoint },
      });
      return res.status(429).json({ error: 'Rate limit exceeded. Please try again later.' });
    }

    await storage.updateRateLimit(clientIP, endpoint);
    next();
  };

  // Vehicle consultation API (replacing Supabase Edge Function)
  app.post('/api/consulta-veiculo', checkRateLimit, async (req: Request, res: Response) => {
    try {
      const { placa, chassis, renavam, tipo }: ConsultaVeicularRequest = req.body;
      const clientIP = getClientIP(req);
      const userAgent = req.headers['user-agent'] || '';

      // Validate required fields
      if (tipo === 'atpv-e') {
        if (!placa || !renavam) {
          return res.status(400).json({ 
            error: 'Para ATPV-E: Placa e RENAVAM são obrigatórios' 
          });
        }
      } else {
        if (!placa) {
          return res.status(400).json({ error: 'Placa é obrigatória' });
        }
      }

      // Validate license plate format
      if (placa) {
        const placaRegex = /^[A-Z]{3}[0-9][A-Z0-9][0-9]{2}$/;
        if (!placaRegex.test(placa.toUpperCase())) {
          return res.status(400).json({ 
            error: 'Formato de placa inválido. Use o formato ABC1234 ou ABC1D23' 
          });
        }
      }

      // Get API access key
      const chaveAcesso = process.env.CHAVE_ACESSO_API;
      if (!chaveAcesso) {
        return res.status(500).json({ 
          error: 'Chave de acesso da API não configurada' 
        });
      }

      // Log the consultation attempt
      await storage.logSecurityEvent({
        action: 'vehicle_consultation_attempt',
        resource_type: 'api',
        ip_address: clientIP,
        user_agent: userAgent,
        details: {
          tipo,
          placa: placa || null,
          renavam: renavam || null,
        },
      });

      // Determine API URL based on consultation type
      let apiUrl = '';
      switch (tipo) {
        case 'gravame':
          apiUrl = 'https://portaldespachantes.online/consultar-gravame';
          break;
        case 'crv-digital':
          apiUrl = 'https://portaldespachantes.online/consultar-crv';
          break;
        case 'base-estadual':
          apiUrl = 'https://portaldespachantes.online/consultar-base-estadual';
          break;
        case 'atpv-e':
          apiUrl = 'https://portaldespachantes.online/consultar-atpve';
          break;
        default:
          return res.status(400).json({ error: 'Tipo de consulta inválido' });
      }

      // Prepare request body
      const requestBody: any = {};
      if (placa) requestBody.placa = placa.toUpperCase();
      if (tipo === 'atpv-e' && renavam) requestBody.renavam = renavam;

      // Make API request with timeout
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000);

      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'chaveAcesso': chaveAcesso,
          'User-Agent': 'MC-Despachante/1.0'
        },
        body: JSON.stringify(requestBody),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        let errorMessage = 'Erro na consulta veicular';
        try {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } catch (e) {
          // Error parsing error response
        }

        await storage.logSecurityEvent({
          action: 'vehicle_consultation_error',
          resource_type: 'api',
          ip_address: clientIP,
          details: {
            error: errorMessage,
            status: response.status,
            tipo,
            placa: placa || null,
          },
        });

        return res.status(response.status).json({ error: errorMessage });
      }

      // Check if response is PDF
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/pdf')) {
        const pdfBuffer = await response.arrayBuffer();
        const base64Pdf = Buffer.from(pdfBuffer).toString('base64');

        await storage.logSecurityEvent({
          action: 'vehicle_consultation_success',
          resource_type: 'api',
          ip_address: clientIP,
          details: {
            tipo,
            placa: placa || null,
            response_type: 'pdf',
          },
        });

        return res.json({
          success: true,
          data: {
            tipo: 'pdf',
            arquivo: base64Pdf,
            contentType: 'application/pdf'
          }
        });
      } else {
        const data = await response.json();

        await storage.logSecurityEvent({
          action: 'vehicle_consultation_success',
          resource_type: 'api',
          ip_address: clientIP,
          details: {
            tipo,
            placa: placa || null,
            response_type: 'json',
          },
        });

        return res.json({
          success: true,
          data: data
        });
      }

    } catch (error) {
      const clientIP = getClientIP(req);

      await storage.logSecurityEvent({
        action: 'vehicle_consultation_internal_error',
        resource_type: 'api',
        ip_address: clientIP,
        details: {
          error_type: 'internal_error',
        },
      });

      return res.status(500).json({ 
        error: 'Erro interno do servidor. Tente novamente.' 
      });
    }
  });

  // AI Assistant API
  app.post('/api/assistente', checkRateLimit, async (req: Request, res: Response) => {
    try {
      const { pergunta, categoria }: AssistenteRequest = req.body;
      const clientIP = getClientIP(req);

      if (!pergunta || pergunta.trim().length === 0) {
        return res.status(400).json({ error: 'Pergunta é obrigatória' });
      }

      const xaiKey = process.env.XAI_API_KEY;
      const openaiKey = process.env.OPENAI_API_KEY;
      const geminiKey = process.env.GEMINI_API_KEY;

      console.log('Verificando chaves disponíveis:', {
        xai: !!xaiKey,
        openai: !!openaiKey, 
        gemini: !!geminiKey
      });

      if (!xaiKey && !openaiKey && !geminiKey) {
        return res.status(500).json({ error: 'Nenhuma API Key configurada' });
      }

      let resposta = '';
      let usedProvider = 'unknown';

      // System message with context about vehicle services
      const systemMessage = `Você é um assistente especializado em serviços veiculares do Brasil. 
Sua especialidade é ajudar pessoas com questões sobre:
- Transferência de propriedade de veículos
- Licenciamento anual (IPVA, DPVAT, taxa de licenciamento)
- Segunda via de documentos (CRV, CRLV, CNH)
- CNH (primeira via, renovação, mudança de categoria)
- Regularização de veículos (débitos, multas, restrições)
- Procedimentos junto ao DETRAN
- Documentação necessária para cada serviço
- Prazos e valores aproximados

Responda de forma clara, prática e em português brasileiro. 
Se não souber algo específico, oriente a procurar um despachante ou o DETRAN.
Mantenha as respostas concisas mas informativas.`;

      // Use Grok as primary API
      if (xaiKey) {
        console.log('Tentando usar Grok como API principal...');
        try {
          const { text } = await generateText({
            model: xai('grok-beta'),
            messages: [
              {
                role: 'system',
                content: systemMessage
              },
              {
                role: 'user',
                content: pergunta
              }
            ],
            maxTokens: 800,
            temperature: 0.7,
          });

          resposta = text || 'Desculpe, não consegui processar sua pergunta.';
          usedProvider = 'grok';

        } catch (grokError: any) {
          console.error('Grok failed, trying Gemini as backup:', grokError?.message || grokError);
          console.error('Grok error details:', grokError);

          // If Grok fails, try Gemini as backup
          if (geminiKey) {
            try {
              const genAI = new GoogleGenerativeAI(geminiKey);
              const model = genAI.getGenerativeModel({ model: 'gemini-1.5-flash' });

              const prompt = `${systemMessage}

Pergunta do usuário: ${pergunta}`;

              const result = await model.generateContent(prompt);
              const response = await result.response;
              resposta = response.text();
              usedProvider = 'gemini';

            } catch (geminiError) {
              console.error('Both Grok and Gemini failed, trying OpenAI:', geminiError);

              // If both fail, try OpenAI as last resort
              if (openaiKey) {
                const openai = new OpenAI({ apiKey: openaiKey });

                const completion = await openai.chat.completions.create({
                  model: "gpt-4o-mini",
                  messages: [
                    {
                      role: "system",
                      content: systemMessage
                    },
                    {
                      role: "user",
                      content: pergunta
                    }
                  ],
                  max_tokens: 800,
                  temperature: 0.7,
                });

                resposta = completion.choices[0]?.message?.content || 'Desculpe, não consegui processar sua pergunta.';
                usedProvider = 'openai';
              } else {
                throw new Error('Todos os serviços de IA falharam');
              }
            }
          } else if (openaiKey) {
            // If no Gemini, try OpenAI directly
            const openai = new OpenAI({ apiKey: openaiKey });

            const completion = await openai.chat.completions.create({
              model: "gpt-4o-mini",
              messages: [
                {
                  role: "system",
                  content: systemMessage
                },
                {
                  role: "user",
                  content: pergunta
                }
              ],
              max_tokens: 800,
              temperature: 0.7,
            });

            resposta = completion.choices[0]?.message?.content || 'Desculpe, não consegui processar sua pergunta.';
            usedProvider = 'openai';
          } else {
            throw grokError;
          }
        }
      } else if (geminiKey) {
        // Use Gemini if no Grok
        const genAI = new GoogleGenerativeAI(geminiKey);
        const model = genAI.getGenerativeModel({ model: 'gemini-1.5-flash' });

        const prompt = `${systemMessage}

Pergunta do usuário: ${pergunta}`;

        const result = await model.generateContent(prompt);
        const response = await result.response;
        resposta = response.text();
        usedProvider = 'gemini';
      } else if (openaiKey) {
        // Use OpenAI as last option
        const openai = new OpenAI({ apiKey: openaiKey });

        const completion = await openai.chat.completions.create({
          model: "gpt-4o-mini",
          messages: [
            {
              role: "system",
              content: systemMessage
            },
            {
              role: "user",
              content: pergunta
            }
          ],
          max_tokens: 800,
          temperature: 0.7,
        });

        resposta = completion.choices[0]?.message?.content || 'Desculpe, não consegui processar sua pergunta.';
        usedProvider = 'openai';
      }

      // Log the assistant usage
      await storage.logSecurityEvent({
        action: 'ai_assistant_query',
        resource_type: 'api',
        ip_address: clientIP,
        user_agent: req.headers['user-agent'] || null,
        user_id: null,
        resource_id: null,
        details: {
          categoria: categoria || 'geral',
          pergunta_length: pergunta.length,
          provider_used: usedProvider,
        },
      });

      return res.json({
        success: true,
        resposta,
        categoria: categoria || 'geral',
        provider: usedProvider
      });

    } catch (error) {
      console.error('AI Assistant error:', error);

      await storage.logSecurityEvent({
        action: 'ai_assistant_error',
        resource_type: 'api',
        ip_address: getClientIP(req),
        user_agent: req.headers['user-agent'] || null,
        user_id: null,
        resource_id: null,
        details: {
          error_type: 'api_error',
        },
      });

      return res.status(500).json({ 
        error: 'Erro ao processar pergunta. Tente novamente.' 
      });
    }
  });

  // Client authentication API
  app.post('/api/auth/cliente', checkRateLimit, async (req: Request, res: Response) => {
    try {
      const { placa, cpf }: ClienteLoginRequest = req.body;
      const clientIP = getClientIP(req);

      if (!placa || !cpf) {
        return res.status(400).json({ error: 'Placa e CPF são obrigatórios' });
      }

      // Find client by placa and CPF
      const cliente = await storage.getClienteByPlacaCpf(placa, cpf);
      if (!cliente) {
        await storage.logAuthAttempt({
          identifier: `${placa}_${cpf}`,
          attempt_type: 'client',
          success: false,
          ip_address: clientIP,
          error_message: 'Cliente não encontrado',
        });
        return res.status(401).json({ error: 'Credenciais inválidas' });
      }

      // Check account status
      if (cliente.account_status === 'suspended' || cliente.account_status === 'locked') {
        return res.status(401).json({ error: 'Conta suspensa ou bloqueada' });
      }

      // Generate JWT token
      const token = jwt.sign(
        { 
          userId: cliente.id, 
          type: 'cliente',
          email: cliente.email 
        },
        JWT_SECRET,
        { expiresIn: '24h' }
      );

      await storage.logAuthAttempt({
        identifier: `${placa}_${cpf}`,
        attempt_type: 'client',
        success: true,
        ip_address: clientIP,
      });

      // Update last login
      await storage.updateCliente(cliente.id, { 
        last_login_at: new Date(),
        login_attempts: 0 
      });

      return res.json({
        success: true,
        token,
        user: {
          id: cliente.id,
          nome: cliente.nome,
          email: cliente.email,
          type: 'cliente'
        }
      });

    } catch (error) {
      return res.status(500).json({ error: 'Erro interno do servidor' });
    }
  });

  // Admin authentication API  
  app.post('/api/auth/admin', checkRateLimit, async (req: Request, res: Response) => {
    try {
      const { email, senha }: AdminLoginRequest = req.body;
      const clientIP = getClientIP(req);

      if (!email || !senha) {
        return res.status(400).json({ error: 'Email e senha são obrigatórios' });
      }

      // Find admin profile
      const profile = await storage.getProfileByEmail(email);
      if (!profile || profile.tipo_usuario !== 'admin') {
        await storage.logAuthAttempt({
          email,
          attempt_type: 'admin',
          success: false,
          ip_address: clientIP,
          error_message: 'Usuário não encontrado',
        });
        return res.status(401).json({ error: 'Credenciais inválidas' });
      }

      // For now, we'll use a simple password check
      // In production, you'd compare against a hashed password
      const isValidPassword = senha === 'admin123'; // Replace with proper password hashing

      if (!isValidPassword) {
        await storage.logAuthAttempt({
          email,
          attempt_type: 'admin',
          success: false,
          ip_address: clientIP,
          error_message: 'Senha inválida',
        });
        return res.status(401).json({ error: 'Credenciais inválidas' });
      }

      const token = jwt.sign(
        { 
          userId: profile.user_id, 
          type: 'admin',
          email: profile.email 
        },
        JWT_SECRET,
        { expiresIn: '8h' }
      );

      await storage.logAuthAttempt({
        email,
        attempt_type: 'admin',
        success: true,
        ip_address: clientIP,
      });

      return res.json({
        success: true,
        token,
        user: {
          id: profile.user_id,
          nome: profile.nome,
          email: profile.email,
          type: 'admin'
        }
      });

    } catch (error) {
      return res.status(500).json({ error: 'Erro interno do servidor' });
    }
  });

  // Get all clients (admin only)
  app.get('/api/clientes', async (req: Request, res: Response) => {
    try {
      // In production, you'd verify JWT token here
      const clientes = await storage.getAllClientes();
      return res.json(clientes);
    } catch (error) {
      return res.status(500).json({ error: 'Erro interno do servidor' });
    }
  });

  // Create new client (admin only)
  app.post('/api/clientes', async (req: Request, res: Response) => {
    try {
      const clienteData = req.body;
      const newCliente = await storage.createCliente(clienteData);
      return res.status(201).json(newCliente);
    } catch (error) {
      return res.status(500).json({ error: 'Erro ao criar cliente' });
    }
  });

  // Get documents for a client
  app.get('/api/clientes/:id/documentos', async (req: Request, res: Response) => {
    try {
      const { id } = req.params;
      const documentos = await storage.getDocumentosByCliente(id);
      return res.json(documentos);
    } catch (error) {
      return res.status(500).json({ error: 'Erro ao buscar documentos' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { eq, and } from "drizzle-orm";
import { db } from "./db";
import { 
  users, 
  profiles, 
  clientes, 
  documentos, 
  security_audit_logs,
  auth_attempts,
  api_rate_limits,
  type User, 
  type InsertUser,
  type Profile,
  type InsertProfile,
  type Cliente,
  type InsertCliente,
  type Documento,
  type InsertDocumento,
  type SecurityAuditLog,
  type AuthAttempt,
  type ApiRateLimit
} from "@shared/schema";

// Enhanced interface with full application CRUD methods
export interface IStorage {
  // Users
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Profiles
  getProfile(userId: string): Promise<Profile | undefined>;
  getProfileByEmail(email: string): Promise<Profile | undefined>;
  createProfile(profile: InsertProfile): Promise<Profile>;
  updateProfile(userId: string, updates: Partial<Profile>): Promise<Profile | undefined>;

  // Clientes
  getCliente(id: string): Promise<Cliente | undefined>;
  getClienteByPlacaCpf(placa: string, cpf: string): Promise<Cliente | undefined>;
  getAllClientes(): Promise<Cliente[]>;
  createCliente(cliente: InsertCliente): Promise<Cliente>;
  updateCliente(id: string, updates: Partial<Cliente>): Promise<Cliente | undefined>;

  // Documentos
  getDocumentosByCliente(clienteId: string): Promise<Documento[]>;
  createDocumento(documento: InsertDocumento): Promise<Documento>;
  updateDocumento(id: string, updates: Partial<Documento>): Promise<Documento | undefined>;

  // Security & Auditing
  logSecurityEvent(log: Omit<SecurityAuditLog, "id" | "created_at">): Promise<void>;
  logAuthAttempt(attempt: Omit<AuthAttempt, "id" | "created_at">): Promise<void>;
  checkRateLimit(ipAddress: string, endpoint: string): Promise<boolean>;
  updateRateLimit(ipAddress: string, endpoint: string): Promise<void>;
}

export class PostgresStorage implements IStorage {
  // Users
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values(insertUser).returning();
    return result[0];
  }

  // Profiles
  async getProfile(userId: string): Promise<Profile | undefined> {
    const result = await db.select().from(profiles).where(eq(profiles.user_id, userId)).limit(1);
    return result[0];
  }

  async getProfileByEmail(email: string): Promise<Profile | undefined> {
    const result = await db.select().from(profiles).where(eq(profiles.email, email)).limit(1);
    return result[0];
  }

  async createProfile(profile: InsertProfile): Promise<Profile> {
    const result = await db.insert(profiles).values(profile).returning();
    return result[0];
  }

  async updateProfile(userId: string, updates: Partial<Profile>): Promise<Profile | undefined> {
    const result = await db.update(profiles)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(profiles.user_id, userId))
      .returning();
    return result[0];
  }

  // Clientes
  async getCliente(id: string): Promise<Cliente | undefined> {
    const result = await db.select().from(clientes).where(eq(clientes.id, id)).limit(1);
    return result[0];
  }

  async getClienteByPlacaCpf(placa: string, cpf: string): Promise<Cliente | undefined> {
    const result = await db.select().from(clientes)
      .where(and(eq(clientes.placa_veiculo, placa), eq(clientes.cpf, cpf)))
      .limit(1);
    return result[0];
  }

  async getAllClientes(): Promise<Cliente[]> {
    return await db.select().from(clientes);
  }

  async createCliente(cliente: InsertCliente): Promise<Cliente> {
    const result = await db.insert(clientes).values(cliente).returning();
    return result[0];
  }

  async updateCliente(id: string, updates: Partial<Cliente>): Promise<Cliente | undefined> {
    const result = await db.update(clientes)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(clientes.id, id))
      .returning();
    return result[0];
  }

  // Documentos
  async getDocumentosByCliente(clienteId: string): Promise<Documento[]> {
    return await db.select().from(documentos).where(eq(documentos.cliente_id, clienteId));
  }

  async createDocumento(documento: InsertDocumento): Promise<Documento> {
    const result = await db.insert(documentos).values(documento).returning();
    return result[0];
  }

  async updateDocumento(id: string, updates: Partial<Documento>): Promise<Documento | undefined> {
    const result = await db.update(documentos)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(documentos.id, id))
      .returning();
    return result[0];
  }

  // Security & Auditing
  async logSecurityEvent(log: Omit<SecurityAuditLog, "id" | "created_at">): Promise<void> {
    await db.insert(security_audit_logs).values(log);
  }

  async logAuthAttempt(attempt: Omit<AuthAttempt, "id" | "created_at">): Promise<void> {
    await db.insert(auth_attempts).values(attempt);
  }

  async checkRateLimit(ipAddress: string, endpoint: string): Promise<boolean> {
    const oneMinuteAgo = new Date(Date.now() - 60 * 1000);
    const result = await db.select().from(api_rate_limits)
      .where(and(
        eq(api_rate_limits.ip_address, ipAddress),
        eq(api_rate_limits.endpoint, endpoint)
      ));

    const totalRequests = result.reduce((sum, record) => {
      return record.created_at > oneMinuteAgo ? sum + record.request_count : sum;
    }, 0);

    return totalRequests < 15; // Allow 15 requests per minute
  }

  async updateRateLimit(ipAddress: string, endpoint: string): Promise<void> {
    const existing = await db.select().from(api_rate_limits)
      .where(and(
        eq(api_rate_limits.ip_address, ipAddress),
        eq(api_rate_limits.endpoint, endpoint)
      ))
      .limit(1);

    if (existing.length > 0) {
      await db.update(api_rate_limits)
        .set({ 
          request_count: existing[0].request_count + 1,
          created_at: new Date()
        })
        .where(eq(api_rate_limits.id, existing[0].id));
    } else {
      await db.insert(api_rate_limits).values({
        ip_address: ipAddress,
        endpoint: endpoint,
        request_count: 1,
      });
    }
  }
}

export const storage = new PostgresStorage();

